INSERT INTO `kalevala_search_texts` (`id`, `record_type`, `record_id`, `public`, `title`, `text`) VALUES
(26, 'Item', 23, 1, 'Yhdestoista runo', 'Yhdestoista runo Lemminkäinen lähtee Saaren suurisukuista neittä kosimaan 1−110. Saaren piiat ensin pilkkaavat häntä, mutta tulevat pian liianki tutuksi hänen kanssansa 111−186. Yhtä Kyllikkiä, jota varten oli lähtenyt, ei saa hyvällä suostumaan, jonka tähden lopulta ryöstää hänen väkisellä, nakka’aa rekeen ja lähtee matkalle 187−272. Kyllikki itkee ja paheksii erittäinki Lemminkäisen sotahalua; Lemminkäinen lupa’aa ei koskaan lähteä sotaan, jos Kyllikki lupa’aisi, ei milloinkaan juoksennella kylässä, ja kumpanenki vannoo pysyäksensä 273−314. Lemminkäisen äiti ihastuu nuoresta miniästänsä 315−402. r11 TEI TEI Kalevalan 1849 painos '),
(7, 'SimplePagesPage', 4, 1, 'Julkaisut', 'Julkaisut  ');
